import base64

class Base64:

    def __init__(self, encoded_hash):
        self.encoded_hash = encoded_hash
        

    def decode_base64_hash(self):
        try:
            # Decodifica el hash en Base64
            self.decoded_hash = base64.b64decode(self.encoded_hash)
            return self.decoded_hash.decode('utf-8')
        except Exception as e:
            return f"Error: {str(e)}"

if __name__ == "__main__":
    encoded_hash = ""
    base_64 = Base64(encoded_hash)

    decoded_hash = base_64.decode_base64_hash()
    print("Hash decodificado:", decoded_hash)